import React, { useState } from 'react';
import { LayoutList, GitGraph, Database, Sun, Moon, Menu, X } from 'lucide-react';

function Header() {
  const [darkMode, setDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Toggle dark mode and apply to document
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  // Toggle mobile menu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white sticky top-0 z-50 shadow-md">
      <div className="max-w-screen-xl mx-auto px-4 sm:px-6 lg:px-8 py-3 flex items-center justify-between">
        {/* Logo + Title (Left Section) */}
        <div className="flex items-center space-x-3 w-1/3">
          <GitGraph className="w-8 h-8 text-indigo-600 dark:text-indigo-400" />
          <h1 className="text-xl font-bold">React App</h1>
        </div>

        {/* Desktop Navigation (Center Section) */}
        <nav className="hidden md:flex items-center justify-center space-x-10 text-sm font-medium w-1/3">
          <a href="#sorting" className="text-blue-600 hover:underline transition-all duration-200">Sorting</a>
          <a href="#graphs" className="text-blue-600 hover:underline transition-all duration-200">Graphs</a>
          <a href="#data-structures" className="text-blue-600 hover:underline transition-all duration-200">Data Structures</a>
        </nav>

        {/* Right Side Controls (Right Section) */}
        <div className="flex items-center justify-end space-x-4 w-1/3">
          {/* Dark Mode Toggle */}
          <button
            onClick={toggleDarkMode}
            className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-all duration-200"
            title="Toggle theme"
            aria-label="Toggle dark mode"
          >
            {darkMode ? (
              <Sun className="w-5 h-5 text-yellow-500" />
            ) : (
              <Moon className="w-5 h-5 text-gray-500" />
            )}
          </button>

          {/* Mobile Menu Toggle */}
          <button
            className="md:hidden p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-all duration-200"
            onClick={toggleMenu}
            aria-label="Toggle mobile menu"
          >
            {isMenuOpen ? (
              <X className="w-5 h-5" />
            ) : (
              <Menu className="w-5 h-5" />
            )}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="md:hidden bg-gray-200 dark:bg-gray-800 px-4 py-3">
          <nav className="flex flex-col space-y-3 text-sm font-medium text-center">
            <a href="#sorting" className="text-blue-600 hover:underline transition-all duration-200" onClick={toggleMenu}>Sorting</a>
            <a href="#graphs" className="text-blue-600 hover:underline transition-all duration-200" onClick={toggleMenu}>Graphs</a>
            <a href="#data-structures" className="text-blue-600 hover:underline transition-all duration-200" onClick={toggleMenu}>Data Structures</a>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
