import React, { useState } from 'react';

function Sidebar({ categories, selectedAlgorithm, onSelect }) {
  const [openCategory, setOpenCategory] = useState(null);

  const handleToggleCategory = (category) => {
    setOpenCategory(openCategory === category ? null : category); // Toggle category
  };

  const handleSelect = (item) => {
    onSelect(item);
  };

  return (
    <aside className="w-full md:w-64 bg-light p-4 border-end">
      <h2 className="text-lg mb-2">Danh Mục Thuật Toán</h2>
      <ul className="list-unstyled">
        {Object.keys(categories).map((category) => (
          <li key={category} className="mb-4">
            <button 
              className="w-full text-left text-dark font-bold"
              onClick={() => handleToggleCategory(category)}
            >
              {category}
            </button>
            {openCategory === category && (
              <ul className="pl-4 mt-2">
                {categories[category].map((item) => (
                  <li key={item} className="mb-2">
                    <button
                      className={`block text-dark hover:text-primary ${selectedAlgorithm === item ? 'font-bold bg-light' : ''}`}
                      onClick={() => handleSelect(item)}
                    >
                      {item}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </aside>
  );
}

export default Sidebar;
