import React, { useState } from 'react';

function Stack() {
  // Sử dụng state để lưu trữ các phần tử trong stack
  const [stack, setStack] = useState([]);
  const [inputValue, setInputValue] = useState('');

  // Hàm thêm phần tử vào stack
  const pushToStack = () => {
    if (inputValue.trim()) {
      setStack([...stack, inputValue]);
      setInputValue('');
    }
  };

  // Hàm xoá phần tử khỏi stack
  const popFromStack = () => {
    if (stack.length > 0) {
      setStack(stack.slice(0, stack.length - 1));
    }
  };

  return (
    <section className="container py-4">
      <h4 className="text-center fw-bold mb-3">Stack - Ngăn Xếp</h4>

      {/* Input và nút để thêm phần tử vào stack */}
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Nhập phần tử"
        />
        <button className="btn btn-success mt-2" onClick={pushToStack}>
          Thêm vào Stack
        </button>
      </div>

      {/* Hiển thị các phần tử trong stack */}
      <div className="mb-3">
        <h5>Các phần tử trong Stack:</h5>
        <ul className="list-group">
          {stack.map((item, index) => (
            <li key={index} className="list-group-item">
              {item}
            </li>
          ))}
        </ul>
      </div>

      {/* Nút để xoá phần tử khỏi stack */}
      <button className="btn btn-danger" onClick={popFromStack}>
        Xoá phần tử cuối cùng
      </button>
    </section>
  );
}

export default Stack;
