import { useState } from 'react';

function Set() {
  // State để lưu trữ Set (tập hợp các mã sản phẩm)
  const [productSet, setProductSet] = useState(new Set());
  const [productId, setProductId] = useState('');
  const [searchId, setSearchId] = useState('');
  const [message, setMessage] = useState('');

  // Thêm mã sản phẩm vào Set
  const addProduct = () => {
    if (!productId) {
      setMessage('Please enter a Product ID');
      return;
    }
    if (productSet.has(productId)) {
      setMessage(`Product ID ${productId} already exists`);
      return;
    }
    // Tạo Set mới bằng spread operator để đảm bảo tính bất biến
    setProductSet(new Set([...productSet, productId]));
    setMessage(`Added product ID: ${productId}`);
    setProductId('');
  };

  // Xóa mã sản phẩm khỏi Set
  const deleteProduct = (id) => {
    if (!productSet.has(id)) {
      setMessage(`Product ID ${id} not found`);
      return;
    }
    const newSet = new Set(productSet);
    newSet.delete(id);
    setProductSet(newSet);
    setMessage(`Removed product ID: ${id}`);
  };

  // Kiểm tra mã sản phẩm
  const checkProduct = () => {
    if (!searchId) {
      setMessage('Please enter a Product ID to check');
      return;
    }
    setMessage(
      productSet.has(searchId)
        ? `Product ID ${searchId} exists`
        : `Product ID ${searchId} not found`
    );
    setSearchId('');
  };

  return (
    <div className="max-w-lg mx-auto bg-white shadow-md rounded-lg p-6 mt-4">
      {/* Tiêu đề */}
      <h1 className="text-xl font-bold text-center mb-4">
        Shopee Affiliate Product ID Set
      </h1>

      {/* Khái niệm về Set */}
      <div className="mb-4">
        <h2 className="text-md font-semibold mb-2">What is a Set?</h2>
        <p className="text-sm text-gray-700">
          A <strong>Set</strong> is a data structure that stores <strong>unique values</strong> without duplicates. It supports:
          <ul className="list-disc pl-5">
            <li><code>add</code>: Add a value (ignores duplicates).</li>
            <li><code>delete</code>: Remove a value.</li>
            <li><code>has</code>: Check if a value exists.</li>
            <li><code>size</code>: Get the number of elements.</li>
          </ul>
          In this component, we use a JavaScript <code>Set</code> to manage unique Shopee Affiliate product IDs.
        </p>
      </div>

      {/* Form thêm mã sản phẩm */}
      <div className="mb-4">
        <h2 className="text-md font-semibold mb-2">Add Product ID</h2>
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Enter Product ID"
            value={productId}
            onChange={(e) => setProductId(e.target.value)}
            className="flex-1 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={addProduct}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Add
          </button>
        </div>
      </div>

      {/* Form kiểm tra mã sản phẩm */}
      <div className="mb-4">
        <h2 className="text-md font-semibold mb-2">Check Product ID</h2>
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="Enter Product ID to check"
            value={searchId}
            onChange={(e) => setSearchId(e.target.value)}
            className="flex-1 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={checkProduct}
            className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
          >
            Check
          </button>
        </div>
      </div>

      {/* Hiển thị thông báo */}
      {message && (
        <p className="mb-4 text-sm text-gray-700">{message}</p>
      )}

      {/* Danh sách mã sản phẩm */}
      <div>
        <h2 className="text-md font-semibold mb-2">Product IDs in Set</h2>
        {productSet.size === 0 ? (
          <p className="text-sm text-gray-500">No product IDs in the set</p>
        ) : (
          <ul className="list-disc pl-5">
            {[...productSet].map((id) => (
              <li key={id} className="flex justify-between items-center py-1">
                <span>{id}</span>
                <button
                  onClick={() => deleteProduct(id)}
                  className="px-2 py-1 bg-red-500 text-white text-sm rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Ứng dụng thực tế */}
      <div className="mt-4">
        <h2 className="text-md font-semibold mb-2">Real-World Application</h2>
        <p className="text-sm text-gray-700">
          This <code>Set</code> component is useful for managing <strong>Shopee Affiliate product IDs</strong>. For example:
          <ul className="list-disc pl-5">
            <li>Track unique product IDs you are promoting to avoid duplicates.</li>
            <li>Quickly check if a product is already in your promotion list.</li>
            <li>Remove products you no longer promote.</li>
          </ul>
          Since a <code>Set</code> ensures no duplicates, it simplifies managing your affiliate inventory efficiently.
        </p>
      </div>
    </div>
  );
}

export default Set;
