import React, { useState } from 'react';

function Sidebar({ categories, selectedAlgorithm, onSelect }) {
  const [openCategory, setOpenCategory] = useState(null);

  const handleToggleCategory = (category) => {
    setOpenCategory(openCategory === category ? null : category); // Toggle category
  };

  const handleSelect = (item) => {
    onSelect(item);
  };

  return (
    <aside className="bg-light border-end p-3 w-100 w-md-25" style={{ height: '100vh', overflowY: 'auto' }}>
      <h4 className="mb-3">Danh Mục Thuật Toán</h4>
      <ul className="list-group">
        {Object.keys(categories).map((category) => (
          <li key={category} className="list-group-item border-0 p-0">
            <button
              className="btn btn-link w-100 text-start text-dark"
              onClick={() => handleToggleCategory(category)}
            >
              <strong>{category}</strong>
            </button>
            {openCategory === category && (
              <ul className="list-unstyled ps-3">
                {categories[category].map((item) => (
                  <li key={item}>
                    <button
                      className={`btn w-100 text-start text-dark p-2 ${selectedAlgorithm === item ? 'bg-info text-white' : ''}`}
                      onClick={() => handleSelect(item)}
                    >
                      {item}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </aside>
  );
}

export default Sidebar;
