import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import MainLayout from './layouts/MainLayout.jsx';
import Home from './components/Home/Home.jsx';
import DataStructure from './components/DataStructure/DataStructure.jsx';
import AlgorithmPage from './components/AlgorithmPage.jsx'; // mới thêm

function App() {
  return (
    <Router>
      <MainLayout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/data-structure" element={<DataStructure />} />

          {/* Dynamic route cho danh mục và thuật toán */}
          <Route path="/:category/:algorithm" element={<AlgorithmPage />} />
        </Routes>
      </MainLayout>
    </Router>
  );
}

export default App;
