import React from 'react';

function Sidebar({ selectedAlgorithm, onSelect }) {
  const renderButton = (label, value) => (
    <button
      type="button"
      className={`block text-blue-600 hover:text-blue-800 ${selectedAlgorithm === value ? 'font-bold bg-gray-300' : ''}`}
      onClick={() => onSelect(value)}
    >
      {label}
    </button>
  );

  return (
    <aside className="w-full md:w-64 bg-gray-200 p-4 overflow-y-auto">
      <h2 className="text-lg mb-2">Algorithm Categories</h2>
      <ul className="list-none">
        <li className="mb-2">
          <h3 className="text-md mb-1">Sorting Algorithms</h3>
          <ul className="pl-4">
            <li>{renderButton('Bubble Sort', 'bubbleSort')}</li>
            <li>{renderButton('Quick Sort', 'quickSort')}</li>
          </ul>
        </li>
        <li className="mb-2">
          <h3 className="text-md mb-1">Searching Algorithms</h3>
          <ul className="pl-4">
            <li>{renderButton('Binary Search', 'binarySearch')}</li>
          </ul>
        </li>
        <li className="mb-2">
          <h3 className="text-md mb-1">Graph Algorithms</h3>
          <ul className="pl-4">
            <li>{renderButton('DFS', 'dfs')}</li>
            <li>{renderButton('BFS', 'bfs')}</li>
            <li>{renderButton('Dijkstra', 'dijkstra')}</li>
          </ul>
        </li>
      </ul>
    </aside>
  );
}

export default Sidebar;
