import React, { useState } from 'react';
import { LayoutList, GitGraph, Database, Moon, Sun } from 'lucide-react';

const Header = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  return (
    <header className="bg-white dark:bg-gray-900 text-gray-900 dark:text-white shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">

        {/* Logo + Tên trang */}
        <div className="flex items-center space-x-3">
          <GitGraph className="w-6 h-6 text-indigo-600 dark:text-indigo-300" />
          <span className="text-xl font-bold">AlgoVisualizer</span>
        </div>

        {/* Menu trung tâm */}
        <nav className="hidden md:flex space-x-6 text-sm font-medium">
          <a href="#sorting" className="flex items-center gap-1 hover:text-indigo-500 transition">
            <LayoutList className="w-4 h-4" /> Sorting
          </a>
          <a href="#graphs" className="flex items-center gap-1 hover:text-indigo-500 transition">
            <GitGraph className="w-4 h-4" /> Graphs
          </a>
          <a href="#data-structures" className="flex items-center gap-1 hover:text-indigo-500 transition">
            <Database className="w-4 h-4" /> Data Structures
          </a>
        </nav>

        {/* Nút chế độ tối/sáng */}
        <button
          onClick={toggleTheme}
          className="ml-4 p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition"
          aria-label="Toggle theme"
        >
          {darkMode ? (
            <Sun className="w-5 h-5 text-yellow-400" />
          ) : (
            <Moon className="w-5 h-5 text-gray-600 dark:text-gray-200" />
          )}
        </button>
      </div>

      {/* Mobile nav (nếu cần sau này) */}
    </header>
  );
};

export default Header;
