import React, { useState } from 'react';
import Header from './Header';
import Sidebar from './Sidebar';
import Footer from './Footer';

function MainLayout({ children }) {
  const [selectedAlgorithm, setSelectedAlgorithm] = useState('');
  const [isSidebarVisible, setIsSidebarVisible] = useState(true);

  const toggleSidebar = () => setIsSidebarVisible(!isSidebarVisible);

  return (
    <div className="bg-light min-vh-100 d-flex flex-column">
      <Header />

      {/* Toggle Button */}
      <div className="bg-white border-bottom px-3 py-2 d-md-none">
        <button className="btn btn-outline-primary" onClick={toggleSidebar}>
          {isSidebarVisible ? 'Ẩn menu' : 'Hiện menu'}
        </button>
      </div>

      <div className="container-fluid flex-grow-1">
        <div className="row">
          {/* Sidebar */}
          {isSidebarVisible && (
            <div className="col-md-3 col-lg-2 mb-4 mb-md-0">
              <Sidebar
                selectedAlgorithm={selectedAlgorithm}
                onSelect={setSelectedAlgorithm}
              />
            </div>
          )}

          {/* Main content */}
          <div className={isSidebarVisible ? 'col-md-9 col-lg-10' : 'col-12'}>
            <main className="p-4 bg-light">
              {React.cloneElement(children, {
                selectedAlgorithm,
                setSelectedAlgorithm,
              })}
            </main>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default MainLayout;
