import React, { useState } from 'react';

function Home({ selectedAlgorithm, setSelectedAlgorithm }) {
  const [inputData, setInputData] = useState('');
  const [extraInput, setExtraInput] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');

  const parseInputData = () => {
    try {
      if (['bubbleSort', 'quickSort'].includes(selectedAlgorithm)) {
        const array = inputData.split(',').map(item => {
          const num = parseInt(item.trim());
          if (isNaN(num)) throw new Error('Invalid number in array');
          return num;
        });
        return array;
      } else if (['dfs', 'bfs', 'dijkstra'].includes(selectedAlgorithm)) {
        const edges = inputData.split(',').map(edge => {
          const [from, to] = edge.trim().split(' ').map(Number);
          if (isNaN(from) || isNaN(to)) throw new Error('Invalid edge format');
          return [from, to];
        });
        return edges;
      } else if (selectedAlgorithm === 'binarySearch') {
        const array = inputData.split(',').map(item => {
          const num = parseInt(item.trim());
          if (isNaN(num)) throw new Error('Invalid number in array');
          return num;
        });
        const target = parseInt(extraInput);
        if (isNaN(target)) throw new Error('Invalid target value');
        return { array, target };
      }
      return null;
    } catch (err) {
      setError(err.message);
      return null;
    }
  };

  const runAlgorithm = () => {
    setError('');
    setResult('');

    const parsedData = parseInputData();
    if (!parsedData) return;

    if (['bubbleSort', 'quickSort'].includes(selectedAlgorithm)) {
      setResult(`Sorted array: ${parsedData.join(', ')} (not implemented)`);
    } else if (selectedAlgorithm === 'binarySearch') {
      setResult(`Found at index: -1 (not implemented)`);
    } else if (['dfs', 'bfs', 'dijkstra'].includes(selectedAlgorithm)) {
      setResult(`Traversal path: (not implemented)`);
    }
  };

  return (
    <div className="container bg-white rounded shadow-sm p-4">
      <h2 className="mb-4 text-primary">
        {selectedAlgorithm ? `Algorithm: ${selectedAlgorithm}` : 'Select an Algorithm'}
      </h2>

      {selectedAlgorithm ? (
        <div>
          <div className="mb-3">
            <label className="form-label fw-bold">Input Data</label>
            <textarea
              className="form-control"
              rows="3"
              value={inputData}
              onChange={(e) => setInputData(e.target.value)}
              placeholder={
                ['bubbleSort', 'quickSort', 'binarySearch'].includes(selectedAlgorithm)
                  ? 'Enter numbers separated by commas (e.g., 5, 2, 9, 1)'
                  : 'Enter edges (e.g., 0 1, 1 2, 2 3)'
              }
            />
          </div>

          {['binarySearch', 'dfs', 'bfs', 'dijkstra'].includes(selectedAlgorithm) && (
            <div className="mb-3">
              <label className="form-label fw-bold">
                {selectedAlgorithm === 'binarySearch' ? 'Target Value' : 'Start Node'}
              </label>
              <input
                type="text"
                className="form-control"
                value={extraInput}
                onChange={(e) => setExtraInput(e.target.value)}
                placeholder={
                  selectedAlgorithm === 'binarySearch'
                    ? 'Enter target value'
                    : 'Enter start node'
                }
              />
            </div>
          )}

          <button className="btn btn-primary" onClick={runAlgorithm}>
            Run Algorithm
          </button>

          {error && (
            <div className="alert alert-danger mt-3" role="alert">
              {error}
            </div>
          )}

          {result && (
            <div className="mt-4">
              <h5>Result</h5>
              <pre className="bg-light p-3 border rounded">{result}</pre>
              <div
                id="visualization"
                className="border rounded bg-white p-3 mt-3"
                style={{ minHeight: '200px' }}
              >
                {/* Placeholder for visual result */}
              </div>
            </div>
          )}
        </div>
      ) : (
        <div className="alert alert-info">
          Please select an algorithm from the sidebar to get started.
        </div>
      )}
    </div>
  );
}

export default Home;
