import React, { useState } from 'react';

function Header({ categories, onSelect }) {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleToggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleSelect = (item) => {
    onSelect(item);
    setIsDropdownOpen(false); // Tắt dropdown khi chọn item
  };

  return (
    <header className="bg-gray-800 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <h1 className="text-2xl">Algorithms & Graphs</h1>
        <div className="relative">
          <button 
            className="bg-blue-600 text-white px-4 py-2 rounded" 
            onClick={handleToggleDropdown}
          >
            Select Algorithm
          </button>
          {isDropdownOpen && (
            <ul className="absolute right-0 mt-2 bg-white text-black rounded shadow-lg w-48">
              {Object.keys(categories).map((category) => (
                <li key={category} className="border-b">
                  <button 
                    className="block text-left px-4 py-2 hover:bg-gray-100"
                    onClick={() => handleSelect(category)}
                  >
                    {category}
                  </button>
                  <ul>
                    {categories[category].map((item) => (
                      <li key={item}>
                        <button
                          className="block text-left px-4 py-2 text-sm hover:bg-gray-100"
                          onClick={() => handleSelect(item)}
                        >
                          {item}
                        </button>
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
