import React, { useState } from 'react';
import { LayoutList, GitGraph, Database, Sun, Moon } from 'lucide-react';

function Header() {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <header className="bg-gradient-to-r from-indigo-900 to-purple-800 text-white sticky top-0 z-50 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">

        {/* Logo + Title */}
        <div className="flex items-center space-x-3">
          <GitGraph className="w-8 h-8 text-pink-300" />
          <h1 className="text-2xl font-bold tracking-wide">AlgoVisualizer</h1>
        </div>

        {/* Navigation Menu */}
        <nav className="hidden md:flex items-center space-x-6 text-sm font-medium">
          <a href="#sorting" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <LayoutList className="w-4 h-4" /> Sorting
          </a>
          <a href="#graphs" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <GitGraph className="w-4 h-4" /> Graphs
          </a>
          <a href="#data-structures" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <Database className="w-4 h-4" /> Data Structures
          </a>
        </nav>

        {/* Dark mode toggle */}
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="bg-white/10 hover:bg-white/20 rounded-full p-2 transition-all"
          title="Toggle theme"
        >
          {darkMode ? (
            <Sun className="w-5 h-5 text-yellow-300" />
          ) : (
            <Moon className="w-5 h-5 text-blue-200" />
          )}
        </button>
      </div>

      {/* Mobile nav - optional */}
      <div className="md:hidden flex justify-around px-4 pb-2 text-sm font-medium">
        <a href="#sorting" className="hover:text-yellow-300">Sorting</a>
        <a href="#graphs" className="hover:text-yellow-300">Graphs</a>
        <a href="#data-structures" className="hover:text-yellow-300">Data</a>
      </div>
    </header>
  );
}

export default Header;
