import React, { useState } from 'react';

function Stack() {
  const [stack,setStack] = useState([]);
  const [inputValue,setInputValue] = useState("");

  const pushToStack = () =>{
    if(inputValue.trim() !== ""){
      setStack((prevStack) => [...prevStack,inputValue.trim()]);
      setInputValue(""); // reset input sau khi add
    }
  }

  const popFromStack = () => {
    setStack((prevStack) => prevStack.slice(0, -1));
  };

  return (
    <section className="container py-4">
      {/* Tiêu đề lý thuyết */}
      <h2 className="text-center fw-bold mb-4">Cấu trúc dữ liệu: Ngăn Xếp (Stack)</h2>

      {/* Phần lý thuyết */}
      <section className="mb-3">
        <h4>1. Lý thuyết về Ngăn Xếp (Stack)</h4>
        <p>
          Ngăn xếp (Stack) là một cấu trúc dữ liệu theo kiểu **LIFO** (Last In, First Out),
          nghĩa là phần tử được thêm vào sau cùng sẽ được lấy ra đầu tiên. Các thao tác cơ bản trên ngăn xếp gồm:
          <strong>push</strong> (thêm phần tử vào cuối), <strong>pop</strong> (xoá phần tử cuối),
          và <strong>peek</strong> (xem phần tử cuối mà không xoá).
        </p>
      </section>

      {/* Phần cú pháp */}
      <section className="mb-3">
        <h4>2. Cú pháp cơ bản</h4>
        <ul>
          <li>
            <strong>push</strong>: Thêm phần tử vào ngăn xếp
            <pre className="bg-light p-3 rounded">
              {`stack.push(element);`}
            </pre>
          </li>
          <li>
            <strong>pop</strong>: Xoá phần tử khỏi ngăn xếp
            <pre className="bg-light p-3 rounded">
              {`stack.pop();`}
            </pre>
          </li>
          <li>
            <strong>peek</strong>: Xem phần tử cuối cùng mà không xoá
            <pre className="bg-light p-3 rounded">
              {`stack[stack.length - 1];`}
            </pre>
          </li>
        </ul>
      </section>

      {/* Ví dụ minh họa */}
      <section className="mb-3">
        <h4>3. Ví dụ minh họa bằng PHP sử dụng stack array</h4>
        <pre className="bg-light p-3 rounded">
          {`<?php

            $stack = [];

            // thêm phần tử vào ngăn xếp (stack)
            array_push($stack, "A");
            array_push($stack, "B");
            array_push($stack, "C");

            // lấy phần tử khỏi ngăn xếp (stack)
            echo array_pop($stack); // "C"
            echo Array_pop($stack); // "B"

            //xem phần tử còn lại trên đỉnh stack
            echo end($stack); // "A"
          `}
        </pre>
      </section>

    </section>
  );
}

export default Stack;
