import React from 'react';
import { LayoutList, GitGraph, Database, Sun, Moon } from 'lucide-react';

function Header() {
  return (
    <header className="bg-gradient-to-r from-indigo-900 to-purple-800 text-white sticky top-0 z-50 shadow-md">
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
        {/* Logo */}
        <div className="flex items-center space-x-3">
          <GitGraph className="w-8 h-8 text-pink-300" />
          <span className="text-2xl font-bold tracking-tight">AlgoVisualizer</span>
        </div>

        {/* Navigation */}
        <nav className="hidden md:flex items-center space-x-6 text-sm font-medium">
          <a href="#sorting" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <LayoutList className="w-4 h-4" /> Sorting
          </a>
          <a href="#graphs" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <GitGraph className="w-4 h-4" /> Graphs
          </a>
          <a href="#data-structures" className="flex items-center gap-1 hover:text-yellow-300 transition-all">
            <Database className="w-4 h-4" /> Data Structures
          </a>
        </nav>

        {/* Right actions: Theme toggle placeholder */}
        <div className="flex items-center space-x-4">
          {/* Theme Toggle (Placeholder logic) */}
          <button className="hover:text-yellow-400 transition-all" aria-label="Toggle theme">
            <Sun className="w-5 h-5" />
          </button>
        </div>
      </div>

      {/* Mobile Nav (optional) */}
      <div className="md:hidden px-6 pb-4 flex justify-around text-sm font-medium">
        <a href="#sorting" className="hover:text-yellow-300">Sorting</a>
        <a href="#graphs" className="hover:text-yellow-300">Graphs</a>
        <a href="#data-structures" className="hover:text-yellow-300">Data</a>
      </div>
    </header>
  );
}

export default Header;
