import React from 'react';

function Sidebar({ selectedAlgorithm, onSelect }) {
  const renderButton = (label, value) => (
    <button
      type="button"
      className={`btn w-100 text-start mb-2 ${
        selectedAlgorithm === value
          ? 'btn-primary fw-bold'
          : 'btn-outline-secondary'
      }`}
      onClick={() => onSelect(value)}
    >
      {label}
    </button>
  );

  return (
    <aside className="bg-white border rounded shadow-sm p-3 h-100">
      <h5 className="mb-3 border-bottom pb-2">Danh mục thuật toán</h5>

      <div className="mb-4">
        <h6 className="text-secondary">Sắp xếp</h6>
        {renderButton('Bubble Sort', 'bubbleSort')}
        {renderButton('Quick Sort', 'quickSort')}
        {renderButton('Merge Sort', 'mergeSort')}
      </div>

      <div className="mb-4">
        <h6 className="text-secondary">Tìm kiếm</h6>
        {renderButton('Binary Search', 'binarySearch')}
        {renderButton('Linear Search', 'linearSearch')}
      </div>

      <div className="mb-4">
        <h6 className="text-secondary">Thuật toán đồ thị</h6>
        {renderButton('DFS', 'dfs')}
        {renderButton('BFS', 'bfs')}
        {renderButton('Dijkstra', 'dijkstra')}
        {renderButton('Kruskal', 'kruskal')}
      </div>
    </aside>
  );
}

export default Sidebar;
