import { useState } from 'react';

function HashTable() {
  // State lưu trữ bảng băm (hash table)
  const [hashTable, setHashTable] = useState({});
  const [key, setKey] = useState('');
  const [value, setValue] = useState('');
  const [message, setMessage] = useState('');

  // Hàm tính chỉ số cho một key (hash function đơn giản)
  const hashFunction = (key) => {
    let hash = 0;
    for (let i = 0; i < key.length; i++) {
      hash = (hash << 5) - hash + key.charCodeAt(i);
      hash = hash & hash; // Đảm bảo hash là số nguyên
    }
    return hash;
  };

  // Thêm phần tử vào bảng băm
  const insert = () => {
    const hash = hashFunction(key);
    setHashTable((prevHashTable) => ({
      ...prevHashTable,
      [hash]: value,
    }));
    setMessage(`Đã thêm key: ${key} với giá trị: ${value}`);
    setKey('');
    setValue('');
  };

  // Tìm kiếm phần tử trong bảng băm
  const search = () => {
    const hash = hashFunction(key);
    if (hashTable[hash]) {
      setMessage(`Tìm thấy key: ${key}, giá trị: ${hashTable[hash]}`);
    } else {
      setMessage(`Không tìm thấy key: ${key}`);
    }
    setKey('');
  };

  // Xóa phần tử khỏi bảng băm
  const remove = () => {
    const hash = hashFunction(key);
    const newHashTable = { ...hashTable };
    delete newHashTable[hash];
    setHashTable(newHashTable);
    setMessage(`Đã xóa key: ${key}`);
    setKey('');
  };

  return (
    <div className="container py-4">
      <h4>Hash Table - Bảng Băm</h4>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Nhập Key"
          value={key}
          onChange={(e) => setKey(e.target.value)}
        />
        <input
          type="text"
          className="form-control mt-2"
          placeholder="Nhập Value"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
        <button className="btn btn-primary mt-2" onClick={insert}>
          Thêm Key-Value
        </button>
        <button className="btn btn-secondary mt-2 ms-2" onClick={search}>
          Tìm Key
        </button>
        <button className="btn btn-danger mt-2 ms-2" onClick={remove}>
          Xóa Key
        </button>
      </div>
      {message && <div className="alert alert-info mt-3">{message}</div>}
      <div className="mt-3">
        <h5>Bảng Băm:</h5>
        <pre>{JSON.stringify(hashTable, null, 2)}</pre>
      </div>
    </div>
  );
}

export default HashTable;
