import React, { useState } from 'react';
import { LayoutList, GitGraph, Database, Sun, Moon, Menu, X } from 'lucide-react';

function Header() {
  const [darkMode, setDarkMode] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    document.documentElement.classList.toggle('dark');
  };

  // Toggle mobile menu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="bg-gradient-to-r from-indigo-900 to-purple-800 text-white sticky top-0 z-50 shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex items-center justify-between">
        {/* Logo + Title */}
        <div className="flex items-center space-x-3">
          <GitGraph className="w-10 h-10 text-pink-400 animate-pulse" />
          <h1 className="text-3xl font-extrabold tracking-tight bg-clip-text text-transparent bg-gradient-to-r from-pink-400 to-yellow-300">
            AlgoMaster
          </h1>
        </div>

        {/* Desktop Navigation */}
        <nav className="hidden lg:flex items-center space-x-8 text-base font-semibold">
          <a
            href="#algorithms"
            className="flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
          >
            <LayoutList className="w-5 h-5" /> Algorithms
          </a>
          <a
            href="#data-structures"
            className="flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
          >
            <Database className="w-5 h-5" /> Data Structures
          </a>
          <a
            href="#visualizations"
            className="flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
          >
            <GitGraph className="w-5 h-5" /> Visualizations
          </a>
        </nav>

        {/* Right Side Controls */}
        <div className="flex items-center space-x-4">
          {/* Dark Mode Toggle */}
          <button
            onClick={toggleDarkMode}
            className="bg-white/10 hover:bg-white/20 rounded-full p-2.5 transition-all duration-300"
            title="Toggle theme"
            aria-label="Toggle dark mode"
          >
            {darkMode ? (
              <Sun className="w-6 h-6 text-yellow-400" />
            ) : (
              <Moon className="w-6 h-6 text-blue-200" />
            )}
          </button>

          {/* Mobile Menu Toggle */}
          <button
            className="lg:hidden p-2 rounded-lg hover:bg-white/10 transition-all duration-300"
            onClick={toggleMenu}
            aria-label="Toggle mobile menu"
          >
            {isMenuOpen ? (
              <X className="w-6 h-6" />
            ) : (
              <Menu className="w-6 h-6" />
            )}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="lg:hidden bg-gradient-to-b from-purple-900 to-indigo-900 px-4 py-6 animate-slide-down">
          <nav className="flex flex-col space-y-4 text-center text-base font-medium">
            <a
              href="#algorithms"
              className="py-2 px-4 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
              onClick={toggleMenu}
            >
              Algorithms
            </a>
            <a
              href="#data-structures"
              className="py-2 px-4 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
              onClick={toggleMenu}
            >
              Data Structures
            </a>
            <a
              href="#visualizations"
              className="py-2 px-4 rounded-lg hover:bg-white/10 hover:text-yellow-400 transition-all duration-300"
              onClick={toggleMenu}
            >
              Visualizations
            </a>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
